SELECT COUNT(*) FROM( SELECT QESTN_NO ,USER_ID ,QESTN_CONTENT ,QESTN_TITLE ,QESTN_PASSWORD ,QESTN_ANSWER ,QESTN_DATE ,QESTN_ANSWER_DATE ,ATCHMNFL_NO
FROM QUESTION ORDER BY QESTN_DATE DESC
) B

SELECT QESTN_NO ,USER_ID ,QESTN_CONTENT ,QESTN_TITLE ,QESTN_PASSWORD ,QESTN_ANSWER ,QESTN_DATE ,QESTN_ANSWER_DATE ,ATCHMNFL_NO
FROM QUESTION
ORDER BY QESTN_DATE DESC LIMIT #{pageCnt} , #{countPerPage}

SELECT QESTN_NO ,USER_ID ,QESTN_CONTENT ,QESTN_TITLE ,QESTN_PASSWORD ,QESTN_ANSWER ,QESTN_DATE ,QESTN_ANSWER_DATE ,ATCHMNFL_NO
FROM QUESTION ORDER BY QESTN_DATE DESC
LIMIT #{pageSize} OFFSET #{startRow}

mapper.insert:

SELECT CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'), LPAD(IFNULL(MAX(SUBSTR(QESTN_NO,10)),0)+1,4,'0'))
FROM QUESTION
WHERE USER_ID = #{userId,jdbcType=VARCHAR}


